import React from 'react';
import './App.css';

class App extends React.Component {
    state = {
        timeData: {
            London: {
                date: '2019-07-08',
                time: '13:00:00'
            },
            China: {
                date: '2019-07-08',
                time: '21:00:00'
            }
        },
        clocks: [
            "London",
            "China"
        ],
        clocksOpts: [
            "London",
            "China",
            "Iraq",
            "France"
        ]

    }

    async getTime() {
        return new Promise(resolve => setTimeout(() => {
            let reqError = Math.random() <= 0.3
            if (reqError) {
                resolve({
                    code: 400,
                    error: '请求出错'
                })
            } else {
                resolve({
                    code: 800,
                    value: new Date()
                })
            }
        }, 800))
    }

    componentDidMount() {
        setInterval(async () => {
            let res = await this.getTime()
            console.log(res)
        }, 60000)
    }

    // renderClocks() {
    //     const { clocks } = this.state
    //     return (
    //         {clocks.map(
    //
    //         )}
    //     )
    // }

    render() {
        return (
            <div className="App">
                <table className="table">
                    <thead>
                    <tr>
                        <th align="center"><strong>London</strong></th>
                        <th align="center"><strong>China</strong></th>
                        <th align="center"><strong>Iraq</strong></th>
                    </tr>
                    </thead>
                    <tbody>
                    <tr>
                        <td align="center">
                            <code>2019-07-08</code>
                            <br></br>
                            <code>13:00:00</code>
                        </td>
                        <td align="center">
                            <code>2019-07-08</code>
                            <br></br>
                            <code>21:00:00</code>
                        </td>
                        <td align="center">
                            <code>2019-07-08</code>
                            <br></br>
                            <code>16:00:00</code>
                        </td>
                    </tr>
                    <tr>
                        <td align="center">
                            <strong>France</strong>
                        </td>
                        <td className="add" align="center">+</td>
                        <td align="center"></td>
                    </tr>
                    <tr>
                        <td align="center">
                            <code>2019-07-09</code>
                            <br></br>
                            <code>00:00:00</code>
                        </td>
                        <td align="center">--</td>
                        <td align="center"></td>
                    </tr>
                    </tbody>
                </table>
            </div>
        )
    }
}

export default App;
