import React from 'react';
import './App.css';

console.log(new Date().getTime())

class App extends React.Component {
    state = {
        timeData: {
            London: {
                date: '2019-07-08',
                time: '13:00:00',
                timezone: 0
            },
            China: {
                date: '2019-07-08',
                time: '21:00:00',
                timezone: 8
            },
            France: {
                date: '2019-07-08',
                time: '21:00:00',
                timezone: 1
            },
            Iraq: {
                date: '2019-07-08',
                time: '21:00:00',
                timezone: 3
            }
        },
        clocks: [
            "London",
            "China",
            "China",
            "China",
        ],
        clocksOpts: [
            "London",
            "China",
            "Iraq",
            "France"
        ]

    }

    async getTime() {
        return new Promise(resolve => setTimeout(() => {
            let reqError = Math.random() <= 0.3
            if (reqError) {
                resolve({
                    code: 400,
                    error: '请求出错'
                })
            } else {
                resolve({
                    code: 800,
                    value: new Date()
                })
            }
        }, 800))
    }

    componentDidMount() {
        setInterval(async () => {
            let res = await this.getTime()
            console.log(res)
        }, 60000)
    }

    renderRowLeftEle(count) {
        let dom = []
        for (let i = 0; i < count; i++) {
            if (i === 0) {
                dom.push(
                    <div className="clock">
                        <div className="city">+</div>
                        <div className="time">--</div>
                    </div>
                )
            } else {
                dom.push(
                    <div className="clock">
                        <div className="city"></div>
                        <div className="time"></div>
                    </div>
                )
            }

        }
        return dom
    }

    render() {
        const {clocks, timeData} = this.state
        const remainder = 3 - clocks.length % 3
        console.log(remainder)
        return (
            <div className="App">
                <div className="table">
                    {
                        clocks.map((city, index) => (
                            <div className="clock" key={index}>
                                <div className="city">{city}</div>
                                <div className="time">
                                    <code>{timeData[city].date}</code>
                                    <br/>
                                    <code>{timeData[city].time}</code>
                                </div>
                            </div>
                        ))
                    }
                    {
                        remainder > 0 ? this.renderRowLeftEle(remainder) : null
                    }

                </div>
            </div>
        )
    }
}

export default App;
