import React from 'react';
import './App.css';

console.log(new Date().getTime())

class App extends React.Component {
    state = {
        timeData: {
            London: {
                date: '2019-07-08',
                time: '13:00:00',
                timezone: 0
            },
            China: {
                date: '2019-07-08',
                time: '21:00:00',
                timezone: 8
            },
            France: {
                date: '2019-07-08',
                time: '21:00:00',
                timezone: 1
            },
            Iraq: {
                date: '2019-07-08',
                time: '21:00:00',
                timezone: 3
            }
        },
        clocks: [
            "London",
            "China",
            "London",
            "China",
            "London",
            "China",
            "London",
            "London",
            "China",
            "London",
            "China",
            "London",
            "London",
            "China",
            "London",
            "China",
            "London",
            "China",
        ],
        clocksOpts: [
            "London",
            "China",
            "Iraq",
            "France"
        ]

    }

    // 模拟获取时间异步请求
    async getTime() {
        return new Promise(resolve => setTimeout(() => {
            let reqError = Math.random() <= 0.3
            if (reqError) {
                resolve({
                    code: 400,
                    error: '请求出错'
                })
            } else {
                resolve({
                    code: 800,
                    value: new Date()
                })
            }
        }, 800))
    }

    // 添加时钟
    addClock() {

    }

    componentDidMount() {
        setInterval(async () => {
            let res = await this.getTime()
            console.log(res)
        }, 60000)
    }

    // 渲染剩余dom，包含add网格
    renderRowLeftEle(count) {
        let dom = []
        for (let i = 0; i < count; i++) {
            if (i === 0) {
                dom.push(
                    <div className="clock add" key={i}>
                        <div className="city">+</div>
                        <div className="time">--</div>
                    </div>
                )
            } else {
                dom.push(
                    <div className="clock" key={i}>
                        <div className="city"></div>
                        <div className="time"></div>
                    </div>
                )
            }

        }
        console.log(dom)
        return dom
    }

    render() {
        const {clocks, timeData} = this.state
        const remainder = 3 - clocks.length % 3
        return (
            <div className="App">
                <div className="table">
                    <div className="block">
                        {
                            clocks.map((city, index) => (
                                <div className="clock" key={index}>
                                    <div className="city">{city}</div>
                                    <div className="time">
                                        <code>{timeData[city].date}</code>
                                        <br/>
                                        <code>{timeData[city].time}</code>
                                    </div>
                                    <span className="close">X</span>
                                </div>
                            ))
                        }
                        <div className="clock add" >
                            <div className="city add-symbol" onClick={() => {this.addClock()}}>+</div>
                            <div className="time">--</div>
                        </div>
                    </div>


                </div>
            </div>
        )
    }
}

export default App;
