import React from 'react';
import { Menu, Dropdown } from 'antd';
import './App.css';


// 获取各时区时间
function getLocalTime(i) {
    //参数i为时区值数字，比如北京为东八区则输进8,西5输入-5
    if (typeof i !== 'number') return;
    var d = new Date();
    //得到1970年一月一日到现在的秒数
    var len = d.getTime();
    //本地时间与GMT时间的时间偏移差
    var offset = d.getTimezoneOffset() * 60000;
    //得到现在的格林尼治时间
    var utcTime = len + offset;
    return new Date(utcTime + 3600000 * i);
}

class App extends React.Component {
    constructor() {
        super()
        // localStorage.setItem('world_clocks_data', JSON.stringify([
        //     {
        //         place: 'London',
        //         timezone: 0
        //     },
        //     {
        //         place: 'China',
        //         timezone: 8
        //     }
        // ]))
        let clocks_localStorage = localStorage.getItem('world_clocks_data')
        let timeData = {}
        if (clocks_localStorage && JSON.parse(clocks_localStorage).length > 0) {
            JSON.parse(clocks_localStorage).forEach((data, index) => {
                timeData[data.place] = {
                    timeStamp: new Date().getLocalTime()
                }
            })
        }
        this.state = {
            adding: false,
            timeData: timeData,
            clocks: (clocks_localStorage && JSON.parse(clocks_localStorage)) || [],
            cities: [
                "London",
                "China",
                "Iraq",
                "France"
            ]
        }
    }

    componentDidMount() {
        setInterval(async () => {
            let res = await this.getTime()
            console.log(res)
        }, 60000)
    }

    // 模拟获取时间异步请求
    async getTime() {
        return new Promise(resolve => setTimeout(() => {
            let reqError = Math.random() <= 0.3
            if (reqError) {
                resolve({
                    code: 400,
                    error: '请求出错'
                })
            } else {
                resolve({
                    code: 800,
                    value: new Date()
                })
            }
        }, 800))
    }

    // 添加时钟
    addClock(item, key, keyPath, selectedKeys, domEvent) {
        // if (this.state.adding) return
        console.log(key)
        console.log('点击增加时钟')
        // this.setState({
        //     adding: true
        // })
    }

    // 渲染剩余dom，包含add网格
    renderRowLeftEle(count) {
        let dom = []
        for (let i = 0; i < count; i++) {
            if (i === 0) {
                dom.push(
                    <div className="clock add" key={i}>
                        <div className="city">+</div>
                        <div className="time">--</div>
                    </div>
                )
            } else {
                dom.push(
                    <div className="clock" key={i}>
                        <div className="city"></div>
                        <div className="time"></div>
                    </div>
                )
            }

        }
        console.log(dom)
        return dom
    }

    renderSelection() {
        const { cities } = this.state
        const menu = (
            <Menu onSelect={() => { this.addClock() }}>
                {
                    cities.map((city, index) => (
                        <Menu.Item key={index}>
                            { city }
                        </Menu.Item>
                    ))
                }
            </Menu>
        );

        return (
            <Dropdown overlay={menu} trigger={['click']}>
                <div className="city add-symbol">
                    +
                </div>
            </Dropdown>
        )
    }

    render() {
        const {clocks, timeData} = this.state
        const remainder = 3 - clocks.length % 3
        return (
            <div className="App">
                <div className="table">
                    <div className="block">
                        {
                            clocks.map((city, index) => (
                                <div className="clock" key={index}>
                                    <div className="city">{city}</div>
                                    <div className="time">
                                        <code>{timeData[city].date}</code>
                                        <br/>
                                        <code>{timeData[city].time}</code>
                                    </div>
                                    <span className="close">X</span>
                                </div>
                            ))
                        }
                        <div className="clock add" >
                            {
                                this.renderSelection()
                            }
                            <div className="time">--</div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default App;
